HassGetState.one_yesno:
{% if query.matched %}
  Ja
{% else %}
  Nej, {{ state.state_with_unit }}
{% endif %}

HassGetState.any:
{% if query.matched %}
  {% set match = query.matched | map(attribute="name") | sort | list %}
  {% if match | length > 4 %}
    Ja, {{ match[:3] | join(", ") }} og {{ (match | length - 3) }} mere
  {%- else -%}
    Ja,
    {% for name in match -%}
      {% if not loop.first and not loop.last %}, {% elif loop.last and not loop.first %} og {% endif -%}
      {{ name }}
    {%- endfor -%}
  {% endif %}
{% else %}
  Nej
{% endif %}

HassGetState.all:
{% if not query.unmatched: %}
  Ja
{% else %}
  {% set no_match = query.unmatched | map(attribute="name") | sort | list %}
  {% if no_match | length > 4 %}
    Nej, ikke {{ no_match[:3] | join(", ") }} og {{ (no_match | length - 3) }} mere
  {%- else -%}
    Nej, ikke
    {% for name in no_match -%}
      {% if not loop.first and not loop.last %}, {% elif loop.last and not loop.first %} og {% endif -%}
      {{ name }}
    {%- endfor %}
  {% endif %}
{% endif %}

HassGetState.which:
{% if not query.matched %}
  Ikke nogen
{% else: %}
  {% set match = query.matched | map(attribute="name") | sort | list %}
  {% if match | length > 4 %}
    {{ match[:3] | join(", ") }} og {{ (match | length - 3) }} mere
  {% else %}
    {%- for name in match -%}
      {% if not loop.first and not loop.last %}, {% elif loop.last and not loop.first %} og {% endif -%}
      {{ name }}
    {%- endfor -%}
  {% endif %}
{% endif %}
